LangManager = {}
local this = LangManager;

local language = require "Language/Language"

function LangManager.GetTxt(key,replace)
	local content = language[key];
	if(content and string.len(content) > 0) then
		if replace then
			return string.format(content,replace)
		else
			return content	
		end
	end
	return key;
end