---@see 飞行道具容器的补充功能_针对不同玩法的差异处理
local CardFlyItemSupplement = {}
local this = CardFlyItemSupplement

function CardFlyItemSupplement:Init()
    this.gameType = ModelList.BattleModel:GetGameType()
end

---@see 设置挂机的飞行道具大小
function CardFlyItemSupplement:SetFlyItemDetailScale(obj)
    if this.gameType ~= PLAY_TYPE.PLAY_TYPE_AUTO_TICKET then
        Anim.scale_to_xy(obj,0.9,0.9,0.4,nil)
    end

end

return this