local GiftPackBonusAndMore = { }
local this = GiftPackBonusAndMore

function GiftPackBonusAndMore:SetData(go, bonus, more)
    local ref = fun.get_component(go, fun.REFER)
    --log.r("GiftPackBonusAndMore "..go.gameObject.name)
    if ref then
        if bonus and bonus > 0 then
            ---log.r("bonus  "..bonus)
            local bonus_obj = ref:Get("bonus")
            fun.set_active(bonus_obj, true)
            local str = tostring(bonus)
            local bonus_txt = ref:Get("bonus_txt")
            bonus_txt.text = str.."%"
        else
            local bonus_obj = ref:Get("bonus")
            fun.set_active(bonus_obj, false)
        end

        if more and #more > 0 and more ~= "0" then
            -- log.r("more  "..more)
            local more_obj = ref:Get("more")
            fun.set_active(more_obj, true)
            local bonus_txt = ref:Get("more_txt")
            bonus_txt.text = more
        else
            local more_obj = ref:Get("more")
            fun.set_active(more_obj, false)
        end
        if (bonus and bonus > 0) or (more and #more > 0 and more ~= "0") then
            fun.set_active(go, true)
        end
    end

end

return this