function GiftPackCompetitionCookieView:on_btn_close_click()
    if this._fsm:GetCurName() == "GiftPackShowState" then return end
    if this.loopTime then
        LuaTimer:Remove(    this.loopTime)
        this.loopTime = nil
    end
    Facade.SendNotification(NotifyName.HideUI, ViewList.GiftPackCompetitionCookieView)
    ModelList.GiftPackModel:CloseView()
end

