        uishiny.enabled = false
    end
end

function  GiftPackCompetitionCookieView:SetUIImageGray(btn_obj,active)
    if active then
        local particle = fun.find_child(btn_obj,"particle")
        if particle then
            fun.set_active(particle,false)
        end
    end
    Util.SetUIImageGray(btn_obj,active)
end


