end

function  GiftPackCompetitionCookieView:GetGiftPos(id)
    if id == product_list[1] then
        local vipexp = fun.find_child(this.cityExp1, "vipexp")
        return   this.left_time.transform.position, this.up_reward_txt1.transform.position, this.up_reward_txt2.transform.position,
        vipexp.transform.position
    elseif id == product_list[2] then
        local vipexp = fun.find_child(this.cityExp2, "vipexp")
        return this.right_time.transform.position,  this.down_reward_icon1.transform.position, this.down_reward_icon2.transform.position, vipexp.transform.position, nil
    else
        local vipexp = fun.find_child(this.cityExp1, "vipexp")
        return this.br_reward_txt.transform.position,this.br_cost_txt.transform.position,
        vipexp.transform.position,nil
    end
end

return this
