local HangUpCardsSummaryItems = BaseChildView:New() --Clazz(BaseChildView,"HangUpCardsSummaryItems")

function HangUpCardsSummaryItems:New(go,reapData,matureNoReap)
    local o = {}
    self.__index = self
    setmetatable(o,self)
    o._data = reapData
    o._matureNoReap = matureNoReap
    o:Init(go)
    return o
end

function HangUpCardsSummaryItems:Init(go)
    self._gameObject = go
    local ref = fun.get_component(go.transform,fun.REFER)
    if ref then
        self.content = ref:Get("content")
        self.img_reap = ref:Get("img_reap")
        --self.text_data = ref:Get("text_data")
    end
    --self.text_data.text = tostring(self._matureNoReap)

    if self._data then
        for k,v in pairs(self._data.cards) do
            local go = fun.get_instance(self.img_reap,self.content)
            if v.sign > 1 then
                local img = go:GetComponent(fun.IMAGE) --fun.get_component(go,fun.IMAGE)
                if img then
                    img.sprite = AtlasManager:GetSpriteByName("BingoAtlas","b_bingo01_small")
                end
            end
            fun.set_active(go,true,0)

            local x = math.modf((v.index-1)/5)
            local y = math.modf((v.index-1)%5)
            fun.set_gameobject_pos(go,29*x,-28*y,0,true)
        end
    end
    --self.img_reap.gameObject:SetActive(false)
end


